mapper.select:
select f.FEEDBACK_ID, PLAN_ID, UNITS_ID, FILE_NAME, FILE_CODE, MAIN_COMPANY_ID, MAIN_COMPANY_NAME, COPY_COMPANY_ID, COPY_COMPANY_NAME, TO_DO_TIME,PRINT_TIME, f.CREATED_BY, f.CREATED_TIME, f.UPDATED_BY, f.UPDATED_TIME, f.ENT_ID ,
FEEDBACK_QUESTIONS_ID, PROBLEM_CLASSIFICATION, PROBLEM_PERFORMANCE, DEPUTY_LEADER_ID, DEPUTY_LEADER_NAME, RECTIFICATION_COMPANY_ID, RECTIFICATION_COMPANY_NAME, MAIN_LEADER_ID, MAIN_LEADER_NAME, RECTIFICATION_STATUS, RECTIFICATION_MEASURES
from
(
  select FEEDBACK_ID, PLAN_ID, UNITS_ID, FILE_NAME, FILE_CODE, MAIN_COMPANY_ID, MAIN_COMPANY_NAME, COPY_COMPANY_ID, COPY_COMPANY_NAME, TO_DO_TIME, PRINT_TIME, CREATED_BY, CREATED_TIME, UPDATED_BY, UPDATED_TIME, ENT_ID
  from inspection_feedback
  where PLAN_ID = #{planId}
  and   UNITS_ID = #{unitsId}
  limit #{pageNum}, #{pageSize}
)f
left join inspection_feedback_questions q on q.FEEDBACK_ID = f.FEEDBACK_ID
