mapper.select:
       select ORGANIZATION_MEETINGS_ID, PLAN_ID, MEETINGS_NAME, MEETINGS_TIME, MEETINGS_PLACE, PARTICIPANTS_ID, PARTICIPANTS_NAME, ORGANIZATION_TYPE, MEETINGS_CONTENT, CREATED_BY, CREATED_TIME, UPDATED_BY, UPDATED_TIME, ENT_ID from inspection_organization_meetings m
left join piis_document doc on concat('OrganizationMeetings', m.ORGANIZATION_MEETINGS_ID) = doc.OBJECT_ID
WHERE ORGANIZATION_MEETINGS_ID = ${organizationMeetingsId}
AND ORGANIZATION_TYPE = #{organizationType}
